import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import index from './index.js'
import ConversationList from './containers/conversationList'
import MessageList from './containers/messageList'
// import Form from './Form'

const API = "http://localhost:3000"
  class App extends Component {
    state = {
      currentUser: null,
      conversations: [],
      messages: []
    }

    componentDidMount(){
      this.fetchConversation()
      this.fetchMessages()
    }

    fetchConversation = () => {
        fetch(API+ '/conversations')
        .then(res => res.json())
        .then(currentConversationData => {
          this.setState({
            conversations: currentConversationData
          })
        })
      }

      fetchMessages = () => {
          fetch(API + '/messages')
          .then(res => res.json())
          .then(currentMessageData => {
            this.setState({
              messages: currentMessageData
            })
          })
        }



      render() {

         return (

           <div className="app">
             <h1> Welcome to Chat App!!</h1>
             <ConversationList
               conversationList={this.state.conversations}/>
             <MessageList messageList={this.state.messsages}/>


           </div>
         );
       }

  }
export default App;
